import React, { useRef, useEffect } from 'react';
import styles from './MainContent.module.css';

function MainContent({ messages, isLoading }) {
    const messagesEndRef = useRef(null);

    const scrollToBottom = () => {
        messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
    };

    useEffect(() => {
        scrollToBottom();
    }, [messages]);

    const formatAssistantMessage = (content) => {
        // Split the content into sections
        const sections = content.split('**').filter(Boolean);
        
        return sections.map((section, index) => {
            if (index % 2 === 0) {
                // Handle regular content and bullet points
                const lines = section.trim().split('\n');
                return lines.map((line, lineIndex) => {
                    if (line.startsWith('*')) {
                        // Convert asterisk to bullet point
                        return (
                            <li key={lineIndex} className={styles.bulletPoint}>
                                {line.substring(1).trim()}
                            </li>
                        );
                    }
                    return line.trim() && (
                        <p key={lineIndex} className={styles.paragraph}>
                            {line.trim()}
                        </p>
                    );
                });
            } else {
                // Handle section headers
                return (
                    <h2 key={`heading-${index}`} className={styles.sectionHeader}>
                        {section.trim()}
                    </h2>
                );
            }
        });
    };

    return (
        <div className={styles.mainContent}>
            {messages.length === 0 && (
                <div className={styles.emptyState}>
                    Upload a PDF and ask questions about it!
                </div>
            )}
            {messages.map((message, index) => (
                <div 
                    key={index} 
                    className={`${styles.message} ${styles[message.type]}`}
                >
                    {message.type === 'user' && (
                        <div className={styles.avatar}>You</div>
                    )}
                    {message.type === 'assistant' && (
                        <div className={styles.avatar}>AI</div>
                    )}
                    <div className={styles.messageContent}>
                        {message.type === 'assistant' ? (
                            <div className={styles.formattedContent}>
                                {formatAssistantMessage(message.content)}
                            </div>
                        ) : (
                            message.content
                        )}
                    </div>
                </div>
            ))}
            {isLoading && (
                <div className={styles.loading}>
                    <div className={styles.loadingDots}>
                        <span></span>
                        <span></span>
                        <span></span>
                    </div>
                </div>
            )}
            <div ref={messagesEndRef} />
        </div>
    );
}

export default MainContent;
