import React, { useRef, useEffect } from 'react';
import styles from './MainContent.module.css';

function MainContent({ messages, isLoading }) {
    const messagesEndRef = useRef(null);

    const scrollToBottom = () => {
        messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
    };

    useEffect(() => {
        scrollToBottom();
    }, [messages]);

    const UserAvatar = () => (
        <div className={styles.avatar}>You</div>
    );

    const AIAvatar = () => (
        <div className="relative flex items-center justify-center">
            <div className="w-9 h-9 rounded-full bg-emerald-500 flex items-center justify-center flex-shrink-0">
                <span className="text-base font-bold text-white lowercase">ai</span>
            </div>
            <div className="absolute -inset-1 rounded-full border-2 border-emerald-500"></div>
        </div>
    );

    return (
        <div className={styles.mainContent}>
            {messages.length === 0 && (
                <div className={styles.emptyState}>
                    Upload a PDF and ask questions about it!
                </div>
            )}
            {messages.map((message, index) => (
                <div 
                    key={index} 
                    className={`${styles.message} ${styles[message.type]}`}
                >
                    {message.type === 'user' ? <UserAvatar /> : <AIAvatar />}
                    <div className={styles.messageContent}>
                        {message.content}
                    </div>
                </div>
            ))}
            {isLoading && (
                <div className={styles.loading}>
                    <div className={styles.loadingDots}>
                        <span></span>
                        <span></span>
                        <span></span>
                    </div>
                </div>
            )}
            <div ref={messagesEndRef} />
        </div>
    );
}

export default MainContent;
