import React, { useRef, useEffect } from 'react';
import styles from './MainContent.module.css';

function MainContent({ messages, isLoading }) {
    const messagesEndRef = useRef(null);

    const scrollToBottom = () => {
        messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
    };

    useEffect(() => {
        scrollToBottom();
    }, [messages]);

    const formatAssistantMessage = (content) => {
        return content.split('\n').map((line, index) => {
            // Updated header check - look for text between colons
            if (line.match(/^(.+):$/)) {
                const headerText = line.replace(':', '');
                return (
                    <div key={index} className={styles.sectionHeader}>
                        {headerText}
                    </div>
                );
            }
            // Check if line is a bullet point
            else if (line.startsWith('*')) {
                const bulletText = line.slice(1).trim();
                return (
                    <div key={index} className={styles.bulletContainer}>
                        <span className={styles.bullet}>•</span>
                        <span className={styles.bulletText}>{bulletText}</span>
                    </div>
                );
            }
            // Regular text
            else if (line.trim()) {
                return (
                    <div key={index} className={styles.paragraph}>
                        {line}
                    </div>
                );
            }
            return null;
        }).filter(Boolean); // Remove null entries
    };

    return (
        <div className={styles.mainContent}>
            {messages.length === 0 && (
                <div className={styles.emptyState}>
                    Upload a PDF and ask questions about it!
                </div>
            )}
            {messages.map((message, index) => (
                <div 
                    key={index} 
                    className={`${styles.message} ${styles[message.type]}`}
                >
                    {message.type === 'user' && (
                        <div className={styles.avatar}>You</div>
                    )}
                    {message.type === 'assistant' && (
                        <div className={styles.avatar}>AI</div>
                    )}
                    <div className={styles.messageContent}>
                        {message.type === 'assistant' ? (
                            <div className={styles.formattedContent}>
                                {formatAssistantMessage(message.content)}
                            </div>
                        ) : (
                            message.content
                        )}
                    </div>
                </div>
            ))}
            {isLoading && (
                <div className={styles.loading}>
                    <div className={styles.loadingDots}>
                        <span></span>
                        <span></span>
                        <span></span>
                    </div>
                </div>
            )}
            <div ref={messagesEndRef} />
        </div>
    );
}

export default MainContent;
